import Link from "next/link";
import styles from "./home.module.css";
import Image from "react-bootstrap/Image";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

function Resume() {
  return (
    <div>
      <Container>
        <Row className={styles.container} id="resume">
          <Col lg={10}>
            <Row className={styles.about}>
              <Col lg={12}>
                <h2>Introduction</h2>
                <p>
                  Yaklaşık 3 yıldır yazılım geliştirmekle ilgileniyorum. Bu süre
                  zarfında çalışmalarım büyük bir kısmını web ve mobil
                  programlama üzerine yürüttüm. Çeşitli sektörlerden birçok
                  firma için kendi tasarladığım responsive, kurumsal web
                  siteleri geliştirdim. Veri akışı ve tarayıcıların çalışma
                  mantığı hakkında daha fazla fikre sahip olmak için de iki ayrı
                  front-end yaklaşımıyla iki blog sitesi geliştirdim. Bunun yanı
                  sıra mobil uygulama geliştirmekle ilgili de bazı çalışmalarım
                  oldu. Flutter öğrenmek için 2020 ara tatilinde bir yazılım
                  kampına katıldım. Tatilden sonra ise kendi üniversitemin bilgi
                  işlem depatmanında yarı zamanlı olarak çalışırken bir mobil
                  uygulama projesinde küçük görevler aldım. Öğrenmenin hiçbir
                  zaman sonu olmayan bir yolda yürümeye benzediğini düşünüyor ve
                  çalışmalarımı bu perpektifle şekillendiriyorum.
                </p>
              </Col>
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Resume;
